import { jsPDF } from 'jspdf';
import { Order, PaymentMethod } from '../types';

function getPaymentMethodLabel(method?: PaymentMethod): string {
  switch (method) {
    case 'card':
      return 'Carte bancaire';
    case 'check':
      return 'Chèque';
    case 'cash':
      return 'Espèces';
    case 'transfer':
      return 'Virement';
    default:
      return 'Non spécifié';
  }
}

export function generateBulkPDF(orders: Order[]) {
  if (orders.length === 0) {
    throw new Error('Aucune commande sélectionnée');
  }

  const doc = new jsPDF();
  const pageWidth = doc.internal.pageSize.getWidth();
  const pageHeight = doc.internal.pageSize.height;
  
  // Logo (vous remplacerez la base64 après)
    const logoImg = 'data:image/png;base64,';
  const logoWidth = 60;
  const logoX = (pageWidth - logoWidth) / 2;

  orders.forEach((order, index) => {
    // Ajouter une nouvelle page sauf pour la première
    if (index > 0) {
      doc.addPage();
    }

    // Logo centré en haut
    doc.addImage(logoImg, 'PNG', logoX, 0, logoWidth, 60);

    // En-tête avec numéro de facture
    doc.setFontSize(14);
    doc.text(`FACTURE n° ${order.invoiceNumber}`, 150, 30);

    // Informations de la facture
    doc.setFontSize(8);
    doc.text(`Date: ${new Date(order.date).toLocaleDateString()}`, 20, 60);
    
    // Informations du client
    doc.setFontSize(12);
    doc.text('Client:', 20, 70);
    doc.setFontSize(12);
    doc.text(order.customerName, 20, 80);
    doc.text(order.address, 20, 85);
    
    let yPos = 90;
    if (order.phone) {
      doc.text(`Tél: ${order.phone}`, 20, yPos);
      yPos += 5;
    }
    if (order.email) {
      doc.text(`Email: ${order.email}`, 20, yPos);
      yPos += 5;
    }
    
    // Statut de paiement et méthode
    if (order.isPaid) {
      doc.setTextColor(0, 128, 0);
      const methodLabel = order.paymentMethod ? ` (${getPaymentMethodLabel(order.paymentMethod)})` : '';
      doc.text(`PAYÉE${methodLabel}`, 150, 40);
    } else {
      doc.setTextColor(255, 0, 0);
      doc.text('NON PAYÉE', 150, 40);
    }
    doc.setTextColor(0, 0, 0);
    
    // En-tête du tableau des produits
    const startY = yPos + 30;
    doc.line(20, startY, 190, startY);
    doc.setFontSize(12);
    doc.text('Produit', 25, startY - 5);
    doc.text('Référence', 120, startY - 5);
    
    // Liste des produits
    let y = startY + 10;
    order.products.forEach((product) => {
      if (y > 250) {
        doc.addPage();
        // Répéter le logo sur la nouvelle page
        doc.addImage(logoImg, 'PNG', logoX, 0, logoWidth, 60);
        y = 80;
      }
      doc.text(product.name, 25, y);
      doc.text(product.reference, 120, y);
      y += 10;
    });
    
    // Total
    doc.line(20, y, 190, y);
    y += 10;
    doc.setFontSize(14);
    doc.text(`Total: ${order.totalAmount.toFixed(2)} €`, 150, y, { align: 'right' });
    
    // Pied de page
    doc.setFontSize(10);
    doc.text('Merci de votre confiance - Contactez moi au 06-33-26-42-92.', 105, pageHeight - 20, { align: 'center' });
  });
  
  // Sauvegarder le PDF avec un nom approprié
  const fileName = orders.length === 1 
    ? `facture-${orders[0].customerName}.pdf`
    : `factures-lot-${orders.length}-commandes.pdf`;
  
  doc.save(fileName);
}